var settings = {
	"async": true,
	"crossDomain": true,
	"url": "https://api.covid19india.org/state_district_wise.json",
	"method": "GET"
}

var values = $.ajax(settings).done(function(response) {
	var result = response["Tamil Nadu"];
	var r = result.districtData["Madurai"];
	var ac , d, rc ;
	ac = r.active;
	d = r.deceased;
	rc = r.recovered;
	console.log(ac, d, rc);
	return [ rc , d , ac];
});
document.getElementById("demo1").home.HTML = values[0];
document.getElementById("demo2").home.HTML = values[1];

document.getElementById("demo3").home.HTML = values[2];


